<div
    x-data="{
        open: false,
        toggle(focusAfter) {
            if (this.open) {
                return this.close(focusAfter);
            }

            this.$refs.button.focus();

            this.open = true;
        },
        close(focusAfter) {
            if (! this.open) return;

            this.open = false;

            focusAfter && focusAfter.focus();
        }
    }"
    x-on:keydown.escape.prevent.stop="close($refs.button)"
    x-on:focusin.window="! $refs.panel.contains($event.target) && close()"
    x-id="['connect-dropdown']" class="relative">
    <!-- Button -->
    <button
        x-ref="button" type="button"
        x-on:click="toggle()" :aria-expanded="open"
        :aria-controls="$id('connect-dropdown')"
        :class="{'rounded-sm': !open}"
        class="px-2 py-1.5 rounded-sm rounded-tl-sm rounded-tr-sm inline-flex items-center justify-between gap-2 bg-{{{$theme}}}-600 hover:bg-{{{$theme}}}-800 text-white menu-link font-semibold relative">

        <div x-show="walletLoading" x-transition>
            <x-theme.spinner square="3" />
        </div>

        <span class="tracking-wide flex items-center gap-2" x-cloak x-show="!!walletBalance" x-transition>
            <span x-text="walletName + ' connected'"  class="text-sm text-slate-200 h-full border-r border-primary-200 border-opacity-50 pr-3 capitalize">
                wallet connected
            </span>
            <span>
                <span x-text="walletBalance"></span>
                <span class="text-slate-100" aria-hidden="true">â‚³</span>
            </span>
        </span>

        <span class="tracking-wide flex gap-2" x-show="!walletBalance" x-transition>
            <span>Connect Wallet</span>
            <span class="text-slate-100" aria-hidden="true">&darr;</span>
        </span>
    </button>

    <!-- Panel -->
    <div
        x-ref="panel"
        x-show="open"
        x-transition.origin.top.left
        x-on:click.outside="close($refs.button)"
        :id="$id('connect-dropdown')"
        style="display: none;"
        class="absolute left-0 mt-0 bg-white rounded-bl-sm rounded-br-sm shadow-md overflow-visible z-40">
        <div class="py-1 flex flex-col gap-2 divide-y divide-slate-800 divide-opacity-40" role="none">
            <a href="#" @click.stop="(open = false); enableWallet('nami');"
                class="text-gray-700 block px-4 py-2 text-xl inline-flex gap-2"
                role="menuitem"
                x-bind:disabled="!supports('nami')"
                @click="activateWallet('nami')"
                tabindex="-1">
                <img alt="nami wallet logo" class="w-6 h-auto"
                        src="data:image/png;base64,"/>
                <span>Nami</span>
                <span class="text-slate-300 text-xs" x-show="!supports('nami')">Not Installed</span>
            </a>

            <a href="#" @click.stop="(open = false); enableWallet('eternl');"
                class="text-gray-700 block px-4 py-2 text-xl inline-flex gap-2"
                role="menuitem"
                x-bind:disabled="!supports('eternl')"
                @click="activateWallet('eternl')"
                tabindex="-1">
                <img alt="eternl wallet logo" class="w-6 h-auto"
                    src="data:image/jpeg;base64,"/>
                <span>Eternl</span>
                <span class="text-slate-300 text-xs" x-show="!supports('eternl')">Not Installed</span>
            </a>

            <a href="#" @click.stop="(open = false); enableWallet('flint');"
                class="text-gray-700 block px-4 py-2 text-xl inline-flex gap-2"
                role="menuitem"
                x-bind:disabled="!supports('flint')"
                @click="activateWallet('flint')"
                tabindex="-1">
                <img alt="flint wallet logo" class="w-6 h-auto"
                    src="data:image/jpeg;base64,"/>
                <span>Flint</span>
                <span class="text-slate-300 text-xs" x-show="!supports('flint')">Not Installed</span>
            </a>

            <a href="#" @click.stop="(open = false); enableWallet('typhoncip30');"
                class="text-gray-700 block px-4 py-2 text-xl inline-flex gap-2 items-center"
                role="menuitem"
                x-bind:disabled="!supports('typhoncip30')"
                @click="activateWallet('typhoncip30')"
                tabindex="-1">
                <img alt="typhon wallet logo" class="w-6 h-auto"
